import express from 'express';
import userRoute from './routes/user.route';
const cors = require('cors');
const fileUpload = require('express-fileupload');

const app = express();
app.use(express.json());
app.use(fileUpload());
app.use(cors({ origin: 'http://localhost:4000' }));

app.use(userRoute);
app.get('/', (_request, response) => {
  response.send({ API_Status: 'ok' });
});

export default app;

import express from 'express';
import userController from '../controller/User.controller';

const route = express.Router();

route.get('/api/users', userController.getUser);
route.post('/api/file', userController.createUser);
route.delete('/api/file', userController.deleteUser);

export default route;

import path from 'path';
import { Request, Response } from 'express';
import UserService from '../service/User.service';
import camelizeKeys from '../utils/camelizeKeysFunc';
import UserBodyType from '../types/UserBodyType';
import IUserBodySnakized from '../interfaces/IUserBodySnakized';

const csv = require('csvtojson');
const fs = require('fs');

const getUser = async (req: Request, res: Response): Promise<Response> => {
  const query = req.query.q as string;
  const response = await UserService.getUser(query);

  if (response.status !== 'SUCCESSFUL') {
    return res.status(400).json(response.data);
  }

  return res.status(response.code).json(response.data);
};

const createUser = async (req: any, res: Response): Promise<Response> => {
  const fileRecived = req.files.csvFile.data;
  let data = Buffer.from(fileRecived, 'utf-8');
  const filePath = path.join(__dirname.split('controller')[0] + '/dbRecords/CSVdata/newCsv.csv');

  fs.writeFile(filePath, data, () => {})

  csv()
  .fromFile(filePath)
  .then(async (jsonObj: IUserBodySnakized[])=>{
    const camelizedObjectArray = camelizeKeys(jsonObj) as UserBodyType[];
    await UserService.createUser(camelizedObjectArray);  
  });

  return res.send({message: 'User successfully registered.'});
}

const deleteUser = async (req: Request, res: Response) => {
  const { body } = req;

  await UserService.deleteUser(body);  

  return res.sendStatus(204);
}

export default {
  getUser,
  createUser,
  deleteUser,
}

import { Op } from 'sequelize';
import User from '../dbRecords/model/User.model';
import UserBodyType from '../types/UserBodyType';
import errors from '../utils/errorMessages';

const getUser = async (query: string) => {
  const result = await User.findAll({ where: {
    [Op.or]: [
      { name: { [Op.like]: `%${query}%` }},
      { city: { [Op.like]: `%${query}%` }},
      { country: { [Op.like]: `%${query}%` }},
      { favoriteSport: { [Op.like]: `%${query}%` }},
    ],
  },
});

  if (result.length === 0) {
    return { status: 'NOT_FOUND', code: 404, data: errors.errorMessage404 };
  }

  return { status: 'SUCCESSFUL', code: 200, data: result };
};

const createUser = async (userArray: UserBodyType[]) => {
  userArray.map(async (user) => {
    if (!user.name || !user.city || !user.country || !user.favoriteSport) {
      return { status: 'INVALID_ENTRY', code: 400, data: errors.errorMessage400 };
    }

    const userResponse = await User.findOne({ where: { name: user.name } });
    const userRegistered = userResponse?.dataValues.name;

    if (user.name === userRegistered) {
      return { status: 'CONFLIT', code: 409, data: errors.errorMessage409 };
    }

    const result = await User.create(user);

    if (!result) {
      return { status: 'NOT_FOUND', code: 404, data: errors.errorMessage404 };
    }

    return { status: 'SUCCESSFUL', code: 200, data: { message: 'User successfully registered.'} };
    });
};

const deleteUser = async (user: UserBodyType) => {

  await User.destroy({ where: { name: user.name }})
};

export default {
  getUser,
  createUser,
  deleteUser,
};

import { DataTypes } from "sequelize";
import database from './index';

const User = database.define('user', {
  id: {
    type: DataTypes.INTEGER,
    allowNull: false,
    primaryKey: true,
    autoIncrement: true,
  },
  name: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  city: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  country: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  favoriteSport: {
    type: DataTypes.STRING,
    allowNull: false,
  },
}, {
  modelName: 'user',
  tableName: 'users',
  timestamps: false,
  underscored: true,
});

export default User;

import { Sequelize } from 'sequelize';
import * as config from '../config/database';

const db = new Sequelize(config);

export default db;

import { Options } from 'sequelize';
import dotenv from 'dotenv';

dotenv.config();

const config: Options = {
  username: process.env.DB_USER || 'root',
  password: process.env.DB_PASSWORD || '123456',
  database: process.env.DB_NAME || 'dbRecords',
  host: process.env.DB_HOST || 'database',
  port: Number(process.env.DB_PORT) || 3306,
  dialect: 'mysql',
};

export = config;

const errorMessage400 = { message: 'Missing information data.' }
const errorMessage404= { message: 'User not found.' };
const errorMessage409 = { message: 'Name already registered.' }

export default {
  errorMessage400,
  errorMessage404,
  errorMessage409,
}

import IUserBodySnakized from '../interfaces/IUserBodySnakized';
import UserBodyType from '../types/UserBodyType';

function camelizeKeys(obj: IUserBodySnakized[]): UserBodyType[] {
  const newObj = obj.map((item: IUserBodySnakized): UserBodyType => {
    return {
      name: item.name,
      city: item.city,
      country: item.country,
      favoriteSport: item.favorite_sport,
    }
  })
  return newObj;
}

export default camelizeKeys;
